import React, { useState } from 'react';
import "./register.css";
import axios from "axios";
const Register = () => {

    const [user,setUser] = useState({

        name:"",
        email:"",
        password:"",
        reEnterPassword:""
    })

    const handleChange = e =>{
        
        const {name,value} = e.target
        setUser({
            ...user,
            [name]: value
        })
    }
    const register =() =>{
        const{name,email,password,reEnterPassword}=user
        if (name && email && password &&(password === reEnterPassword)) {
            
            axios.post("http://localhost:9002/register",user)
            .then(res => console.log(res))
        }else{
            alert("valid input")
        }
       
    }
    return (

        <div className="register">
        {console.log("user",user)}
        <h1>Register</h1>
        <input type="text" name="name" value={user.name} placeholder="Your name " onChange={handleChange}></input>
        <input type="text" name="email" value={user.email} placeholder="Your email" ></input>
        <input type="password" value={user.password} name="password" ></input>
        <input type="password" value={user.reEnterPassword} name="reEnterPassword" ></input>
        <div className="button" onClick={register} >Register</div>
        <div>or</div>
        <div className="button" >Login</div>
    </div>
    );
}

export default Register;
